CREATE TABLE SBG_TEASER ( ID VARCHAR2(40) NOT NULL , TITLE VARCHAR2(175) NOT NULL, SUMMARY VARCHAR2(4000), DESCRIPTION CLOB, MEDIA_ITEM VARCHAR2(40), LINK_TEXT VARCHAR2(175), LINK_TARGET VARCHAR2(40), LINK_TO VARCHAR2(50), LINK_TO_CHANNEL VARCHAR2(40), LINK_TO_CONTENT VARCHAR2(40), LINK_TO_URL VARCHAR2(1024), PRIMARY KEY (ID) );
CREATE TABLE SBG_BANNER ( ID VARCHAR2(40) NOT NULL, TITLE VARCHAR2(175) NOT NULL, SUMMARY VARCHAR2(4000), DESCRIPTION CLOB, MEDIA_ITEM VARCHAR2(40), LINK_TEXT VARCHAR2(175), LINK_TARGET VARCHAR2(40), LINK_TO VARCHAR2(50), LINK_TO_CHANNEL VARCHAR2(40), LINK_TO_CONTENT VARCHAR2(40), LINK_TO_URL VARCHAR2(1024), PRIMARY KEY (ID) );
CREATE TABLE SBG_CONTENT_LIST ( ID VARCHAR2(40) NOT NULL, TITLE VARCHAR2(175) NOT NULL, SUMMARY VARCHAR2(2000), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_LINKS ( ID VARCHAR2(40) NOT NULL, PARENT_ID VARCHAR2(40), LINK_TEXT VARCHAR2(175), LINK_TARGET VARCHAR2(40), LINK_CLASS_NAME VARCHAR2(175), LINK_TO VARCHAR2(50), LINK_TO_CHANNEL VARCHAR2(40), LINK_TO_CONTENT VARCHAR2(40), LINK_TO_URL VARCHAR2(1024), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_REFERENCE_DATA ( ID VARCHAR2(40) NOT NULL, TITLE VARCHAR2(175), SUMMARY VARCHAR2(1024), PRIMARY KEY (ID) );
CREATE TABLE SBG_REFERENCE_OPTIONS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), LABEL VARCHAR2(100), VALUE VARCHAR2(100), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_GENERIC ( ID VARCHAR2(40), TITLE VARCHAR2(175), DESCRIPTION CLOB, PRIMARY KEY (ID) );
CREATE TABLE SBG_COUNTRY_OFFICE (ID VARCHAR2(40) NOT NULL, TITLE VARCHAR2(175), FRIENDLY_NAME VARCHAR2(175), SUMMARY VARCHAR2(2000), INTRODUCTION VARCHAR2(4000), SBG_OVERVIEW VARCHAR2(40), DESCRIPTION CLOB, TOP_BANNER VARCHAR2(40), LOCATION VARCHAR2(175), HEAD_OFFICE_ADDRESS VARCHAR2(40), PRIMARY KEY (ID) );
CREATE TABLE SBG_COUNTRY_ADDRESS ( ID VARCHAR2(40), BANK_NAME VARCHAR2(175), ADDRESS CLOB, COUNTRY VARCHAR2(40), LINKED_LIST VARCHAR2(40), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_CONTACTS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), CONTACT_ID VARCHAR2(40), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_INDIVIDUAL_CONTACT ( ID VARCHAR2(40), FIRST_NAME VARCHAR2(80), SURNAME VARCHAR2(80), DESIGNATION VARCHAR2(175), INTRODUCTION VARCHAR2(2000), TELEPHONE_NUMBER1 VARCHAR2(175), TELEPHONE_NUMBER2 VARCHAR2(175), EMAILID VARCHAR2(175), COUNTRY VARCHAR2(40), CONTINENT VARCHAR2(40), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_PRODUCTS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), PRODUCT_ID VARCHAR2(200), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_BUSINESS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), BUSINESS_ID VARCHAR2(200), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_TERMS ( ID VARCHAR2(40), TITLE VARCHAR2(100), FRIENDLY_NAME VARCHAR2(175), INTRODUCTION VARCHAR2(2000), SBG_OVERVIEW VARCHAR2(40), DESCRIPTION CLOB, PRIMARY KEY (ID) );
CREATE TABLE SBG_GENERAL_PAGE ( ID VARCHAR2(40), TITLE VARCHAR2(100), INTRODUCTION VARCHAR2(2000), SBG_GENERIC VARCHAR2(40), DESCRIPTION CLOB, TOP_BANNER VARCHAR2(40), MAIN_TAB_TITLE VARCHAR2(175), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_TABS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), TITLE VARCHAR2(40), TYPE VARCHAR2(40), FRIENDLY_NAME VARCHAR2(40), DESCRIPTION CLOB, CHANNEL_ID VARCHAR2(40), EXTERNAL_URL VARCHAR2(1024), ORDER_BY NUMBER, PRIMARY KEY (ID) );
CREATE TABLE SBG_CONTACT_LIST ( ID VARCHAR2(40), TITLE VARCHAR2(100), PRIMARY KEY (ID) );
CREATE TABLE SBG_FAQ_CATEGORY ( ID VARCHAR2(40), TITLE VARCHAR2(100), INTRODUCTION VARCHAR2(2000), SBG_GENERIC VARCHAR2(40), DESCRIPTION CLOB, PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_CATEGORY ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), TITLE VARCHAR2(40), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_FAQ ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), QUESTION VARCHAR2(175), ANSWER CLOB, ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_PRODUCT ( ID VARCHAR2(40), TITLE VARCHAR2(100), INTRODUCTION VARCHAR2(2000), SBG_GENERIC VARCHAR2(40), DESCRIPTION CLOB, TOP_BANNER VARCHAR2(40), MAIN_TAB_TITLE VARCHAR2(175), PRIMARY KEY (ID) );
CREATE TABLE SBG_SECTOR ( ID VARCHAR2(40), TITLE VARCHAR2(100), INTRODUCTION VARCHAR2(2000), SBG_GENERIC VARCHAR2(40), DESCRIPTION CLOB, TOP_BANNER VARCHAR2(40), MAIN_TAB_TITLE VARCHAR2(175), PRIMARY KEY (ID) );
CREATE TABLE SBG_PRESS_RELEASE ( ID VARCHAR2(40) NOT NULL, TITLE VARCHAR2(175), INTRODUCTION VARCHAR2(2000), SBG_GENERIC VARCHAR2(40), LIVE_DATE DATE, DESCRIPTION CLOB, BANNER VARCHAR2(40), CONTACT_ID VARCHAR2(40), MARKETING_REGION VARCHAR2(175), BUSINESS_SEGMENT VARCHAR2(175), META_KEYWORDS VARCHAR2(2000), PRIMARY KEY (ID) );
CREATE TABLE SBG_RELATED_COUNTRIES ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), COUNTRY_ID VARCHAR2(200), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );
CREATE TABLE SBG_IFRAME_COMPONENT ( ID VARCHAR2(40) NOT NULL, NAME VARCHAR2(768), DESCRIPTION VARCHAR2(3000), URL VARCHAR2(3000), DISPLAYVIEWID VARCHAR2(40), TITLE VARCHAR2(3000), HEADER VARCHAR2(3000), FOOTER VARCHAR2(3000), TTL VARCHAR2(400), THUMBNAIL VARCHAR2(40), RENDERINGTYPE CHAR(1) DEFAULT '0' NOT NULL, PRIMARY KEY (ID));
CREATE TABLE SBG_RELATED_SECTORS ( ID VARCHAR2(40), PARENT_ID VARCHAR2(40), SECTOR_ID VARCHAR2(40), ORDER_BY NUMBER(10), PRIMARY KEY (ID) );

CREATE TABLE SBG_ACCOLADES(
	ID VARCHAR2(40) PRIMARY KEY,	
	SHORT_DESCRIPTION VARCHAR2(500), 
	PUBLICATION VARCHAR2(500), 
	CAPTURE_DATE DATE,
	FULL_DESCRIPTION CLOB, 
	LATEST_YEAR NUMBER,	
	PRESS_RELEASE VARCHAR2(40), 
	COMMENTS VARCHAR2(1000) 
);

CREATE TABLE SBG_RELATED_PUBLICATION_YEAR(
	ID VARCHAR2(40) PRIMARY KEY, 
	PARENT_ID VARCHAR2(40), 
	PUBLICATION_YEAR_ID VARCHAR2(10), 
	ORDER_BY  NUMBER 
);

CREATE TABLE SBG_DEALS(
	ID VARCHAR2(40) PRIMARY KEY,
	TITLE VARCHAR2(500),
	SUMMARY VARCHAR2(1000),
	DEAL_DATE DATE,
	CLIENT_NAME VARCHAR2(100),
	DEAL_AMOUNT VARCHAR2(100),
	CURRENCY VARCHAR2(100),
	DESCRIPTION CLOB,
	COMMENTS VARCHAR2(500),
	PRESS_RELEASE VARCHAR2(40),
	THUMBNAIL_IMAGE VARCHAR2(40),
	IMAGE VARCHAR2(40)	
);
CREATE TABLE SBG_VIEW_COMPONENT ( ID VARCHAR2(40) NOT NULL, NAME VARCHAR2(768), DESCRIPTION VARCHAR2(3000), DISPLAYVIEWID VARCHAR2(40), DATA VARCHAR2(3000), TITLE VARCHAR2(3000), HEADER VARCHAR2(3000), FOOTER VARCHAR2(3000), RENDERINGTYPE VARCHAR2(1), TTL VARCHAR2(400), THUMBNAIL VARCHAR2(40), CACHE_STRATEGY VARCHAR2(500), PRIMARY KEY (ID) );

CREATE TABLE SBG_CHANNEL_DESCRIPTOR (
	ID VARCHAR2(40) PRIMARY KEY,
	NAVIGATION_COLUMNS NUMBER(2),
	NAVIGATION_ALIGNMENT VARCHAR2(50),
	NAVIGATION_COLUMN_NUMBER NUMBER(2),
	SUB_NAVIGATION_ITEMS VARCHAR2(50),
	NAVIGATION_PLACEHOLDER_CHANNEL VARCHAR2(10)
);